import { useEffect, useState} from 'react';
import axios from 'axios';

function List() {

  const [tapu, setTapu] = useState([]);
  const [isLoading, setIsloading] = useState(true); 

  useEffect(()=>{
    axios("https://2696e52c-ce6f-43cc-bbbe-46850ab2d273.mock.pstmn.io//api")
      .then((res) => setTapu(res.data))
      .catch((e) => console.log(e))
      .finally(() => setIsloading(false));
}, []);

  return (
    <>
      {isLoading && <div>Loading...</div>}

      <div className="d-flex flex-column h-70">

        {
          tapu.map((tapus)=>(
            <div className="card">
              <div className="card-content">
                <img src={tapus.url} width="100" alt="" />
                <div className="d-flex flex-column">
                  <div className="name">{tapus.name}</div>
                  <small className="title">{tapus.title}</small>
                  <div className="d-flex">
                    <div className="rank">{tapus.rank}</div>
                    <div className="location">{tapus.location}</div>
                  </div>
                </div>
              </div>
              <button type="button">Sepete Ekle</button>
            </div>
          ))
        }
      </div>
    </>
  )
}

export default List;
