import { useQuery } from "react-query";
import fetchProduct from "./Auth/api";
import { useBasket } from "../contexts/BasketContext";
import Card from "../Components/Card";
import Basket from "../Components/Basket";

function List() {
  const { isLoading, error, data } = useQuery(["products"], fetchProduct);
  const { addToBasket, items } = useBasket();

  if (isLoading)
    return (
      <div className="lds-ring">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>
    );

  if (error) {
    return <div>Error</div>;
  }

  const findBasketItem = items.find((item) => item.id === item.id);

  return (
    <>
      <div className="d-flex flex-column h-70-vh overflow-auto p-2-1r border-bottom-1">
        {data.map((item) => (
          <Card />
        ))}
      </div>

      <Basket />
    </>
  );
}

export default List;
