import {useState} from 'react';
import { useFormik, Formik, Form, Field } from "formik";
import * as yup from 'yup';
import fetchRegister from '../api';

const validationSchema = yup.object().shape({
    email: yup.string().email("Geçerli bir email girin").required("Zorunlu alan"),
    password: yup.string().min(5, "Parolanız en az 5 karakter olmalıdır").required("Zorunlu alan")
});

function Signup() {

    const formik = useFormik({
        initialValues: {
            email: "",
            password: ""
        },
        validationSchema,
        onSubmit: (values, bag) =>{
            try{
                localStorage.setItem("input", JSON.stringify(values));
                console.log(values);
            }catch(e){
                bag.setErrors({ general: e.response.data.message})
            }
        }
    });

  return (
    <div>
        <Formik
       initialValues={{
         firstName: '',
         lastName: '',
         email: '',
       }}
       validationSchema={SignupSchema}
       onSubmit={values => {
         // same shape as initial values
         console.log(values);
       }}
     >
       {({ errors, touched }) => (
         <Form>
           <Field name="firstName" />
           {errors.firstName && touched.firstName ? (
             <div>{errors.firstName}</div>
           ) : null}
           <Field name="lastName" />
           {errors.lastName && touched.lastName ? (
             <div>{errors.lastName}</div>
           ) : null}
           <Field name="email" type="email" />
           {errors.email && touched.email ? <div>{errors.email}</div> : null}
           <button type="submit">Submit</button>
         </Form>
       )}
     </Formik>
    </div>
  )
}

export default Signup


