import React from 'react';
import { useFormik } from "formik";
import validationSchema from './validation';


function Signup() {

    const formik = useFormik({
        initialValues: {
            email: "",
            password: ""
        },
        validationSchema,
        onSubmit: async (values, bag) => {
            console.log(values);
        }
    })

  return (
    <div>
        <form onSubmit={formik.handleSubmit}>
            <div className="d-flex flex-column m-2-1r">
                <label htmlFor="email">E-Posta</label>
                <input type="text" placeholder="E-Mail" name="email" className="p-15-0 mb-30" 
                    onChange={formik.handleChange} 
                    onBlur={formik.handleBlur} 
                    value={formik.values.email}
                    onInvalid={formik.errors.email}
                    />

                <input type="password" placeholder="Password" name="password" className="p-15-0 mb-30"
                    onChange={formik.handleChange} 
                    onBlur={formik.handleBlur} 
                    value={formik.values.password}/>

                <select name="locale" className="p-15-0 mb-30">
                    <option value="turkce">Türkçe</option>
                </select>
                <div className="d-flex">
                    <button type='submit' className="w-100 m-2-1r p-15-0">Sign Up</button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default Signup
