import React from "react";
import { useBasket } from "../contexts/BasketContext";
function Basket() {
  const { items } = useBasket();

  console.log(items[0]);
  return (
    <>
      {items.array.forEach((item) => {
        <div key={i} className="d-flex flex-column p-1r min-h-200">
          <h3 className="products-total font-size-1_5r p-5-0">
            Ürünlerin Toplamı:
          </h3>
          <small className="total font-size-0_9r p-5-0">
            Toplam:{item[0].price}
          </small>
          <small className="tax-cargo font-size-0_9r p-5-0">
            Vergiler + Kargo:{item[0].cargoPrice + item[0].tax}
          </small>
          <div className="total-payment font-size-0_9r font-weight-bold p-5-0">
            Genel Toplam:{item[0].price + item[0].cargoPrice + item[0].tax}
          </div>
        </div>;
      })}
    </>
  );
}

export default Basket;
