import { useState, createContext, useEffect, useContext } from "react";

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [loggedIn, setLoggedIn] = useState(false);

  const login = (data) => {
    setLoggedIn(() => {
      localStorage.setItem("setLoggedIn", true);
    });
    setEmail(data.email);
    setPassword(data.password);
  };

  //   useEffect(() => {
  //     localStorage.setItem("loggedIn", JSON.stringify(setLoggedIn()));
  //   }, []);

  const values = {
    loggedIn,
    email,
    password,
    login,
  };

  localStorage.setItem("setLoggedIn", JSON.stringify(setLoggedIn(true)));
  const value = localStorage.getItem("setLoggedIn");
  console.log(JSON.parse(value) === true);

  return <AuthContext.Provider value={values}>{children}</AuthContext.Provider>;
};

const useAuth = () => useContext(AuthContext);

export { AuthProvider, useAuth };
