import "./sass/main.scss";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useTranslation } from "react-i18next";
import trFlag from "./img/turkish.png";
import enFlag from "./img/english.png";
//pages
import Account from "./pages/Account";
import ProductList from "./pages/ProductList";
import Navbar from "./Components/Navbar";

function App() {
  const { t, i18n } = useTranslation();

  const handleChangeLng = (lng) => {
    i18n.changeLanguage(lng);
    localStorage.setItem("lng", lng);
  };
  return (
    <div className="App">
      <Router>
        <div className="d-flex">
          <button onClick={() => handleChangeLng("tr")}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              id="flag-icons-tr"
              viewBox="0 0 512 512"
            >
              <g fill-rule="evenodd">
                <path fill="#e30a17" d="M0 0h512v512H0z" />
                <path
                  fill="#fff"
                  d="M348.8 264c0 70.6-58.3 127.9-130.1 127.9s-130.1-57.3-130.1-128 58.2-127.8 130-127.8S348.9 193.3 348.9 264z"
                />
                <path
                  fill="#e30a17"
                  d="M355.3 264c0 56.5-46.6 102.3-104.1 102.3s-104-45.8-104-102.3 46.5-102.3 104-102.3 104 45.8 104 102.3z"
                />
                <path
                  fill="#fff"
                  d="m374.1 204.2-1 47.3-44.2 12 43.5 15.5-1 43.3 28.3-33.8 42.9 14.8-24.8-36.3 30.2-36.1-46.4 12.8-27.5-39.5z"
                />
              </g>
            </svg>
          </button>
          <button onClick={() => handleChangeLng("en")}>
            <img width="50" src={enFlag} alt="english-flag" />
          </button>
        </div>
        <Navbar />
        <Switch>
          <Route path="/ProductList" component={ProductList} />
          <Route path="/" component={Account} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
