import { useState } from "react";
import { useFormik, Formik, Form, Field } from "formik";
import * as yup from "yup";
import fetchRegister from "../api";

const validationSchema = yup.object().shape({
  email: yup.string().email("Geçerli bir email girin").required("Zorunlu alan"),
  password: yup
    .string()
    .min(5, "Parolanız en az 5 karakter olmalıdır")
    .required("Zorunlu alan"),
});

function Signup() {
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema,
    onSubmit: (values, bag) => {
      try {
        localStorage.setItem("input", JSON.stringify(values));
        console.log(values);
      } catch (e) {
        bag.setErrors({ general: e.response.data.message });
      }
    },
  });

  return (
    <>
      <Formik
        initialValues={{
          email: "",
          password: "",
        }}
        validationSchema={validationSchema}
        onSubmit={(values) => {
          localStorage.setItem("input", JSON.stringify(values));
        }}
      >
        {({ errors, touched }) => (
          <Form onSubmit={formik.handleSubmit}>
            <div className="d-flex flex-column m-2-1r">
              <label htmlFor="email">E-Posta</label>
              {errors.email && touched.email ? <div>{errors.email}</div> : null}
              <Field
                type="text"
                placeholder="E-Mail"
                name="email"
                className="p-15-0 mb-30"
              />
              {errors.password && touched.password ? (
                <div>{errors.password}</div>
              ) : null}
              <Field
                type="password"
                placeholder="Password"
                name="password"
                className="p-15-0 mb-30"
              />

              <select name="locale" className="p-15-0 mb-30">
                <option value="tr">Türkçe</option>
                <option value="ing">İngilizce</option>
              </select>
              <div className="d-flex">
                <button type="submit" className="w-100 m-2-1r p-15-0">
                  Sign Up
                </button>
              </div>
            </div>
          </Form>
        )}
      </Formik>
      ;
    </>
  );
}

export default Signup;
