import {useState} from 'react';
import { useFormik } from "formik";
import * as yup from 'yup';
import fetchRegister from '../api';

import { useAuth } from './AuthContext';

const validation = yup.object().shape({
    email: yup.string().email("Geçerli bir email girin").required("Zorunlu alan"),
    password: yup.string().min(5, "Parolanız en az 5 karakter olmalıdır").required("Zorunlu alan")
});

function Signup() {

    const {login} = useAuth();

    const formik = useFormik({
        initialValues: {
            email: "",
            password: ""
        },
        validation,
        onSubmit: (values, bag) =>{
            try{
                localStorage.setItem("input", JSON.stringify(values));
            }catch(e){
                bag.setErrors({ genearl: e.response.data.message })
            }
        }
        // onSubmit: async (values, bag) => {
        //     console.log(values);
        //     try{
        //     const registerResponse = fetchRegister({ email: values.email, password: values.password});
        //     console.log(registerResponse);
        //     }catch(e){
        //         bag.setErrors({ general: e.response.data.message })
        //     }
        // },
    });

  return (
    <div>
        <form onSubmit={formik.handleSubmit}>
            <div className="d-flex flex-column m-2-1r">
                {
                    formik.errors.general && (
                        <div>{formik.errors.general}</div>
                    )
                }
                <label htmlFor="email">E-Posta</label>
                <input type="text" placeholder="E-Mail" name="email" className="p-15-0 mb-30" 
                    onChange={formik.handleChange} 
                    onBlur={formik.handleBlur} 
                    value={formik.values.email}
                    isinvalid={formik.touched.email && formik.errors.email}
                    />

                <input type="password" placeholder="Password" name="password" className="p-15-0 mb-30"
                    onChange={formik.handleChange} 
                    onBlur={formik.handleBlur} 
                    value={formik.values.password}
                    isinvalid={formik.touched.password && formik.errors.password}
                    />

                <select name="locale" className="p-15-0 mb-30">
                    <option value="tr">Türkçe</option>
                    <option value="ing">İngilizce</option>
                </select>
                <div className="d-flex">
                    <button type='submit' className="w-100 m-2-1r p-15-0">Sign Up</button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default Signup
