import { Field } from "formik";
import Signup from "./Auth/Signup";
import { useAuth } from "../contexts/AuthContext";

function Account() {
  const { loggedIn } = useAuth();

  console.log(loggedIn);
  return (
    <>
      <h1 className="font-size-1_5r p-2-1r">Account</h1>
      {!loggedIn && (
        <>
          <Signup />
        </>
      )}
      {loggedIn && (
        <>
          <div className="m-2-1r">
            <h1>İsim Soyisim</h1>
            <div className="d-flex flex-column border-bottom-1">
              <div className="p-15-0 w-100">E-mail: </div>
              <div className="p-15-0 w-100">Password: </div>
              <div className="p-15-0 w-100">Current locale: </div>
            </div>
            <select className="p-15-0 mb-30 w-100 mt-20">
              <option placeholder="Lokal Seç" value="null">
                Lokal Seçiniz
              </option>
              <option value="Tr">Türkçe</option>
              <option value="En">İngilizce</option>
              <option value="fr">Fransızca</option>
              <option value="OTHER">Other</option>
            </select>
          </div>
        </>
      )}
    </>
  );
}

export default Account;
