import {useQuery} from 'react-query';
import Card from '../Components/Card';

function List() {

  const { isLoading, error, data } = useQuery(['products'], () =>
    fetch('https://2696e52c-ce6f-43cc-bbbe-46850ab2d273.mock.pstmn.io//api').then(res =>
      res.json()
    )
  )

  if (isLoading) return '<div className="lds-ring"><div></div><div></div><div></div><div></div></div>'

  if (error) return 'An error has occurred: ' + error.message;

  console.log("data", data);

  return (
    <>
      {/* {isLoading && } */}

      {
        data.map((tapus) =>(
          <Card key={tapus.id} item={tapus}/>
        ))
      }


      <div className="d-flex flex-column p-1r min-h-200">
        <h3 className="products-total font-size-1_5r p-5-0">Ürünlerin Toplamı: </h3>
        <small className="total font-size-0_9r p-5-0">Toplam:</small>
        <small className="tax-cargo font-size-0_9r p-5-0">Vergiler + Kargo: </small>
        <div className="total-payment font-size-0_9r font-weight-bold p-5-0">Genel Toplam: </div>
      </div>
    </>
  )
}

export default List;
