import React from "react";
import { useBasket } from "../contexts/BasketContext";
function Basket() {
  const { items } = useBasket();

  const total = items.reduce((acc, obj) => acc + obj.price, 0);

  console.log(items);
  return (
    <>
      {items.map((item) => (
        <div key={item.id} className="d-flex flex-column p-1r min-h-200">
          <h3 className="products-total font-size-1_5r p-5-0">
            Ürünlerin Toplamı:
          </h3>
          <small className="total font-size-0_9r p-5-0">Toplam:{total}</small>
          <small className="tax-cargo font-size-0_9r p-5-0">
            Vergiler + Kargo:{item.cargoPrice + item.tax}
          </small>
          <div className="total-payment font-size-0_9r font-weight-bold p-5-0">
            Genel Toplam:{item.price + item.cargoPrice + item.tax}
          </div>
        </div>
      ))}
    </>
  );
}

export default Basket;
