import { useQuery } from "react-query";
import Card from "../Components/Card";
import fetchProduct from "./Auth/api";

function List() {
  const { isLoading, error, data } = useQuery(["products"], fetchProduct);

  if (isLoading)
    return (
      <div className="lds-ring">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>
    );

  if (error) return "An error has occurred: " + error.message;

  console.log("data", data);

  return (
    <>
      <div className="d-flex flex-column h-70-vh overflow-auto p-2-1r border-bottom-1">
        {data.map((item) => (
          <Card key={item.id} item={item} />
        ))}
      </div>

      <div className="d-flex flex-column p-1r min-h-200">
        <h3 className="products-total font-size-1_5r p-5-0">
          Ürünlerin Toplamı:{" "}
        </h3>
        <small className="total font-size-0_9r p-5-0">Toplam:</small>
        <small className="tax-cargo font-size-0_9r p-5-0">
          Vergiler + Kargo:{" "}
        </small>
        <div className="total-payment font-size-0_9r font-weight-bold p-5-0">
          Genel Toplam:{" "}
        </div>
      </div>
    </>
  );
}

export default List;
